import java.math.*;
import java.util.*;
/**
 * The solution is wrong, however the idea seems to be correct, but I still don't know how to make 2^(2^31) fast.
 * Idea: consider adding an Nth point.
 * We draw a line from the Nth point to any other point 1, 2, ..., N-1
 * How many new pieces of land a new connection will generate?
 * 1<->N: +1
 * 2<->N: +(N-2)
 * ...
 * N-2<->N: +(N-2)
 * N-1<->N: +1
 * It appears to be a Pascal's triangle.
 */
public class HowManyPiecesOfLand {

    public static final BigInteger TWO = new BigInteger("2");
    public static final long DIVIDER = 100000L;
    public static final BigInteger BI_POW2_100000 = new BigInteger("");

    public static void main(String[] args){

        final Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		for(int i = 0; i < n; i++){
			long num = sc.nextLong()-1;

            BigInteger res = pow2(num);
			System.out.println(res);
		}
	}

    private static BigInteger pow2(long num) {
        return pow(new BigInteger(String.valueOf(num)), TWO);
    }
    private static BigInteger pow(BigInteger num, BigInteger pow) {

        BigInteger res = BigInteger.ONE;
        while(num.compareTo(BigInteger.ZERO) > 0){
            if(num.mod(TWO).equals(BigInteger.ONE)){
                res = res.multiply(pow);
            }
            num = num.divide(TWO);
            pow = pow.multiply(pow);
        }

        return res;
    }
}
